mapper.select:
SELECT * FROM ( SELECT ROWNUM no, m.* FROM ( SELECT * FROM members
mid LIKE '%${v}%' OR email LIKE '%${v}%' OR phone LIKE '%${v}%' OR address LIKE '%${v}%'
ORDER BY NAME asc) m ) WHERE no BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE members SET name = #{name}, email = #{email}, zipcode = #{zipcode}, address = #{address}, phone = #{phone}

	, photo = # {photo}


WHERE mid = #{mid} and pwd = #{pwd}
